mapper.select:
select distinct u.id, u.username, u.display_name , u.created_by from surpass_userinfo u where u.id not in( select rm.member_id from surpass_roles r,surpass_role_member rm where r.deleted = 'n'
and rm.role_id = #{Dto.roleId} and r.id = #{Dto.roleId}
and r.role_name = #{Dto.roleName}
and rm.type in( 'USER','USER-DYNAMIC') and rm.role_id = r.id )
and u.username = #{Dto.username}
and u.display_name like CONCAT ('%', #{Dto.displayName}, '%')

select distinct r.* from surpass_roles r where r.id not in( select rm.role_id from surpass_role_member rm,surpass_userinfo u where rm.member_id = u.id
and u.username = #{Dto.username}
and rm.member_id = #{Dto.memberId}
and u.deleted = 'n' )
and r.role_name like CONCAT('%', #{Dto.roleName}, '%')
and r.deleted = 'n'

mapper.delete:
delete gm from surpass_role_member gm where type = 'user-dynamic' and gm.roleid=#{id} and not exists( select 1 from surpass_userinfo u where u.deleted = 'n' and u.id=gm.memberid
and (${filters})
)
